# 从 URL query 参数中获取我们自己传递的 "fileName" 字段
query_params = item.get("json", {}).get("query")
file_name = query_params.get("fileName")

# 新的、更清晰的错误检查
if not file_name:
    raise ValueError(f"无法从 URL Query 参数中找到 fileName 字段。收到的 Query 为: {query_params}")

# 检查二进制数据是否存在，如果不存在也给出警告（但流程继续）
binary_data = item.get("binary")
if not binary_data:
    raise ValueError("警告: 未在输入中找到二进制文件数据(binary property)。后续文件操作节点可能会失败。")


# 定义你的文件部署映射
file_map = {
    "ts-service-5.0.jar": {
        "targetDir": "/iflytek/server/zhft-4.0-mysql/ts-service-inner/ts-service-5.0/ts-service-5.0",
        "port":9191,
    },
    "aicourt-monitor-service-1.0.jar": {
        "targetDir": "/iflytek/server/zhft-4.0-mysql/aicourt-monitor-service-1.0",
        "port":7901,
    },
    "aicourt-video-service-1.0.jar": {
        "targetDir": "/iflytek/server/zhft-4.0-mysql/aicourt-video-service-1.0",
        "port":7902,
    }
}

if file_name not in file_map:
    raise ValueError(f"不支持的文件名: {file_name}。请在 Code 节点的 file_map 中添加配置。")

config = file_map[file_name]
target_dir = config["targetDir"]
service_port = config["port"] 
restart_script = f"{target_dir}/restartup.sh"

tempFileName = binary_data.get("data",{}).get("fileName")
return {
    "fileName": file_name,
    "targetDir": target_dir,
    "restartScript": restart_script,
    "tempFilePath": f"/tmp/{tempFileName}",
    "tempFileName" :tempFileName,
    "port": service_port,
    "binary": binary_data
}

